  const [posts, setPosts] = useState([]);

  const createPost = (newPost) =>
    setPosts((currentPost) => [
      newPost,
      ...currentPost,
    ])

  const deletePost = (indexToDelete) =>
    setPosts((currentPost) =>
    currentPost.filter((post, index) => index !== indexToDelet))

